mapper.select:

select 

from tb_orderpay
where orderPayId = #{orderpayid,jdbcType=CHAR}
  and orderId = #{orderid,jdbcType=VARCHAR}
  and producer_id = #{producerId,jdbcType=VARCHAR}
  and storeId = #{storeid,jdbcType=CHAR}
  and payId = #{payid,jdbcType=CHAR}
  and addressId = #{addressid,jdbcType=CHAR}
  and agreeID = #{agreeid,jdbcType=CHAR}

mapper.delete:

delete from tb_orderpay
where orderPayId = #{orderpayid,jdbcType=CHAR}
  and orderId = #{orderid,jdbcType=VARCHAR}
  and producer_id = #{producerId,jdbcType=VARCHAR}
  and storeId = #{storeid,jdbcType=CHAR}
  and payId = #{payid,jdbcType=CHAR}
  and addressId = #{addressid,jdbcType=CHAR}
  and agreeID = #{agreeid,jdbcType=CHAR}

